import styled from "styled-components";

const Container = styled.div`
  border: 5px solid #44045f;
  margin: 5px;
  padding-bottom: 20px;
  //align-items: center;
  background-color: #3a0850;
  color: beige;
  border-radius: 8%;
  width: 20%;
  height: 450px;
  position: relative;
  font-size: 90%;
`;

const Buttons = styled.button`
  border-radius: 5px;
  margin: 10px;
  padding: 5px;
  background-color: #03f907;
  color: rgb(5, 90, 33);
  position: absolute;
  left: 83%;
`;

const Name = styled.h2`
  position: absolute;
  bottom: 2%;
  left: 7%;
  color: black;
  background-color: #03f907;
  border-radius: 5px;
  z-index:3;
`;

const Img = styled.img`
  display: flex;
  flex-wrap: wrap;
  position: absolute;
  bottom:5%;
  left:0;
  right: 0;
  z-index:1;
  margin: auto;
  width: 90%;
  height: 50%;
  border-radius: 8px;

