const regexEmail = new RegExp('/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i');
const regexPassword = new RegExp('/^[A-Za-z]\w{7,14}$/');
let errors = {};
const validation = ({ email, password }) => {

  if (!regexEmail.test(email)) {
    errors.email = "Debe ser un correo electrónico";
  }
  if (!email) {
    errors.email = "Esta campo no puede estar vacio";
  }
  if (email.length > 35) {
    errors.email = "Debe tener menos de 35 caracteres";
  }
  if (!regexPassword.test(password)) {
    errors.password =
      "Password debe tener al menos un numero, una letre mayuscula y un caracter especial";
  }
  if (password.length < 6 || password.length > 10) {
    errors.email = "Debe tener entre 6 y 10 caracteres";
  }
};


export default validation
